$(document).ready(function(){$(".go-to").click(function(a){a.preventDefault();$("html, body").animate({scrollTop:$($(this).attr("href")).offset().top},500)});$("#contactForm").submit(function(f){f.preventDefault();var a=$(this);var d=a.find('[type="submit"]');var c=d.text();var b=a.find(".alert");$.ajax({url:"",type:"post",dataType:"json",data:a.serialize(),beforeSend:function(){d.prop("disabled",true).text("Sending...")},success:function(e){if(e.hasOwnProperty("type")&&e.hasOwnProperty("message")){b.removeClass("alert-success alert-danger").addClass("alert-"+e.type).text(e.message).show();if(e.type=="success"){d.prop("disabled",true).text("Sent!")}else{d.prop("disabled",false).text(c)}}else{d.prop("disabled",false).text(c)}},error:function(h,e,g){d.prop("disabled",false).text(c);console.error(g)}})})});